CACHE_JS = function(args, callback) {
    var attrs = args[0],
        src = args[1],
        text = args[2],
        disabled,
        assetEl;

    attrs[VAR_DATA_SRC] = src;

    assetEl = CLONE_ELEMENT(SCRIPT_ELEMENT, attrs);

    if ('text' in assetEl) {
        assetEl.text = text;
    } else {
        APPEND_CHILD(assetEl, doc.createTextNode(text));
    }

    callback(assetEl);
}