CAPTURE_CSS = function(args, callback) {
    var nodeName = args[0],
        node = args[1];

    if (nodeName == VAR(VAR_STYLE) ||
        (nodeName == VAR(VAR_LINK) && LOWERCASE(GET_ATTR(node, VAR_REL) || '').trim() == VAR(VAR_STYLESHEET))) {
        callback(GET_ATTR(node, VAR_HREF), VAR_HREF);
    }
}