select = e => document.querySelector(e);
selectAll = e => document.querySelectorAll(e);

const base = select('.base');

function resize() {
	let vh = window.innerHeight;
	let sh = base.offsetHeight;
	let scaleFactor = vh/sh;
	if(scaleFactor<1) {
		gsap.set(base, { scale: scaleFactor });
	}
	else {
        gsap.set(base, { scale: 1 });
    }
}

window.onload = () => {
    window.onresize = resize;
};

resize();
